import React, { useState, useEffect } from "react";
import axios from 'axios';
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import AddNote from "./AddNote";

function App() {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/notes')
      .then(response => {
        setNotes(response.data);
      })
      .catch(error => console.error('Error fetching data: ', error));
  }, []);


  // using the data as the id. specifically using the timestamp.
  // const addNote = (newNote) => {
  //   setNotes((prevNotes) => {
  //     return [...prevNotes, { ...newNote, id: Date.now() }];
  //   });
  // };
  const addNote = (newNote) => {
    // Ensure newNote has content before sending
    if (newNote.title.trim() === "" && newNote.content.trim() === "") {
      console.error('Empty note not added.');
      return;
    }

    axios.post('http://localhost:5000/notes', newNote)
      .then(response => {
        console.log('Response data:', response.data); // This will output the response data to the console
        setNotes(prevNotes => [...prevNotes, response.data]);
      })
      .catch(error => {
        console.error('Error posting data: ', error);
        console.log('Error response data:', error.response.data); // This will output the error response data to the console, if available
      });
  };




  // filtering out the note according to it id.
  // const deleteNote = (id) => {
  //   setNotes((prevNotes) => {
  //     return prevNotes.filter((note) => note.id !== id);
  //   });
  // };
  const deleteNote = (id) => {
    console.log("delete for id", id)
    axios.delete(`http://localhost:5000/notes/${id}`)
      .then(() => {
        setNotes(prevNotes => prevNotes.filter(note => note._id !== id));
      })
      .catch(error => console.error('Error deleting data: ', error));
  };

  return (
    <div>
      <Header />
      <AddNote onAdd={addNote} />
      {notes.map((note) => (
        <Note
          key={note.id}
          id={note.id}
          title={note.title}
          content={note.content}
          onDelete={deleteNote}
        />
      ))}
      <Footer />
    </div>
  );
}

export default App;
